    graph = [[] for _ in range(n)]
    for u, v in edges:
        graph[u].append(v)
        graph[v].append(u)

    def dfs(node, parent, path_values):
        max_length = 1
        min_nodes = 1

        for neighbor in graph[node]:
            if neighbor == parent:
                continue

            new_path_values = path_values.copy()
            new_path_values[nums[neighbor]] = new_path_values.get(nums[neighbor], 0) + 1

            valid_path = all(count <= 2 for count in new_path_values.values())

            if valid_path:
                sub_length, sub_nodes = dfs(neighbor, node, new_path_values)

                if sub_length + 1 > max_length:
                    max_length = sub_length + 1
                    min_nodes = sub_nodes + 1
                elif sub_length + 1 == max_length:
                    min_nodes = min(min_nodes, sub_nodes + 1)

        return max_length, min_nodes

    overall_max_length = 0
    overall_min_nodes = float('inf')

    for start in range(n):
        path_values = {nums[start]: 1}
        length, nodes = dfs(start, -1, path_values)

        if length > overall_max_length:
            overall_max_length = length
            overall_min_nodes = nodes
        elif length == overall_max_length:
            overall_min_nodes = min(overall_min_nodes, nodes)

    return overall_max_length, overall_min_nodes

