class Employee:
    def __init__(self, name):
        self.name = name
        self.manager = None
        self.direct_reports = []
        self.level = 0
        self.total_subordinates = 0

class OrganizationAnalyzer:
    def __init__(self):
        self.employees = {}
        self.ceo = None

    def add_employee(self, name, manager_name=None):
        if name not in self.employees:
            self.employees[name] = Employee(name)

        if manager_name:
            if manager_name not in self.employees:
                self.employees[manager_name] = Employee(manager_name)

            self.employees[name].manager = self.employees[manager_name]
            self.employees[manager_name].direct_reports.append(self.employees[name])

    def set_ceo(self, name):
        self.ceo = self.employees[name]

    def calculate_hierarchy_levels(self):
        def dfs_level(employee, current_level):
            employee.level = current_level
            for report in employee.direct_reports:
                dfs_level(report, current_level + 1)

        if self.ceo:
            dfs_level(self.ceo, 1)

    def count_total_subordinates(self):
        def dfs_subordinates(employee):
            total_subs = len(employee.direct_reports)
            for report in employee.direct_reports:
                total_subs += dfs_subordinates(report)
            employee.total_subordinates = total_subs
            return total_subs

        if self.ceo:
            dfs_subordinates(self.ceo)

    def get_manager_chain(self, employee_name):
        employee = self.employees[employee_name]
        chain = []
        current = employee.manager
        while current:
            chain.append(current.name)
            current = current.manager
        return chain

    def print_organization_details(self):
        for name, employee in self.employees.items():
            print(f"Employee: {name}")
            print(f"  Level: {employee.level}")
            print(f"  Total Subordinates: {employee.total_subordinates}")
            print(f"  Manager Chain: {self.get_manager_chain(name)}")
            print()

