def find_organizational_root(employees):
    employee_set = set(emp.name for emp in employees)
    manager_set = set(emp.manager for emp in employees if emp.manager)

    root_name = list(employee_set - manager_set)[0]
    return next(emp for emp in employees if emp.name == root_name)

