    def __init__(self, name, role, manager=None):
        self.name = name
        self.role = role
        self.manager = manager
        self.subordinates = []

    def add_subordinate(self, employee):
        self.subordinates.append(employee)
        employee.manager = self

class Organization:
    def __init__(self):
        self.employees = {}

    def add_employee(self, name, role, manager_name=None):
        employee = Employee(name, role)
        self.employees[name] = employee

        if manager_name:
            manager = self.employees.get(manager_name)
            if manager:
                manager.add_subordinate(employee)

    def get_manager_hierarchy(self, employee_name):
        employee = self.employees.get(employee_name)
        if not employee:
            return None

        hierarchy = []
        current = employee
        while current.manager:
            hierarchy.append(current.manager.name)
            current = current.manager
        return hierarchy

    def get_subordinates(self, employee_name):
        employee = self.employees.get(employee_name)
        if not employee:
            return None

        def collect_subordinates(emp):
            subs = []
            for sub in emp.subordinates:
                subs.append(sub.name)
                subs.extend(collect_subordinates(sub))
            return subs

        return collect_subordinates(employee)

    def get_organizational_level(self, employee_name):
        hierarchy = self.get_manager_hierarchy(employee_name)
        return len(hierarchy) if hierarchy is not None else 0

