class OrganizationAnalyzer:
    def __init__(self):
        self.employees = {}
        self.departments = {}

    def add_employee(self, name, department, salary):
        if department not in self.departments:
            self.departments[department] = []

        self.employees[name] = {
            'department': department,
            'salary': salary
        }
        self.departments[department].append(name)

    def get_department_average_salary(self, department):
        if department not in self.departments:
            return 0

        dept_employees = self.departments[department]
        total_salary = sum(self.employees[emp]['salary'] for emp in dept_employees)
        return total_salary / len(dept_employees) if dept_employees else 0

    def get_highest_paid_employee(self):
        if not self.employees:
            return None

        return max(self.employees, key=lambda x: self.employees[x]['salary'])

    def get_department_employee_count(self, department):
        return len(self.departments.get(department, []))

    def list_departments(self):
        return list(self.departments.keys())

