def findValidSplit(nums, conflictingPairs):
    def is_valid_split(pairs):
        graph = [[] for _ in range(len(nums) + 1)]
        for a, b in pairs:
            graph[a].append(b)
            graph[b].append(a)

        visited = set()
        def dfs(node):
            if node in visited:
                return False
            visited.add(node)
            for neighbor in graph[node]:
                if not dfs(neighbor):
                    return False
            return True

        for i in range(1, len(nums) + 1):
            if i not in visited:
                if not dfs(i):
                    return False
        return True

    for i in range(len(conflictingPairs)):
        test_pairs = conflictingPairs[:i] + conflictingPairs[i+1:]
        if is_valid_split(test_pairs):
            return i

    return -1

