def findValidSplit(nums, conflictingPairs):
    n = len(nums)
    graph = [[] for _ in range(n)]
    degree = [0] * n

    for a, b in conflictingPairs:
        graph[a-1].append(b-1)
        graph[b-1].append(a-1)
        degree[a-1] += 1
        degree[b-1] += 1

    def can_split(mid):
        left = nums[:mid]
        right = nums[mid:]

        left_set = set(left)
        right_set = set(right)

        for a, b in conflictingPairs:
            if (a in left_set and b in left_set) or (a in right_set and b in right_set):
                return False
        return True

    left, right = 1, n
    while left < right:
        mid = (left + right) // 2
        if can_split(mid):
            right = mid
        else:
            left = mid + 1

    return left

