    high_gc_samples = []
    for sample in samples:
        sequence = sample['dna_sequence'].upper()
        gc_count = sequence.count('G') + sequence.count('C')
        gc_percentage = gc_count / len(sequence)
        if gc_percentage > threshold:
            high_gc_samples.append(sample['sample_id'])
