def analyze_dna_sequences(samples):
    species_sequence_count = {}

    for sample in samples:
        species = sample['species']
        sequence = sample['dna_sequence']

        if species not in species_sequence_count:
            species_sequence_count[species] = {
                'total_sequences': 0,
                'unique_sequences': set()
            }

        species_sequence_count[species]['total_sequences'] += 1
        species_sequence_count[species]['unique_sequences'].add(sequence)

    results = []
    for species, data in species_sequence_count.items():
        results.append({
            'species': species,
            'total_sequences': data['total_sequences'],
            'unique_sequence_count': len(data['unique_sequences'])
        })

    return results

