def count_nucleotides(dna_sequence):
    nucleotide_counts = {
        'A': dna_sequence.count('A'),
        'C': dna_sequence.count('C'),
        'G': dna_sequence.count('G'),
        'T': dna_sequence.count('T')
    }
    return nucleotide_counts

def gc_content(dna_sequence):
    gc_count = dna_sequence.count('G') + dna_sequence.count('C')
    total_length = len(dna_sequence)
    return (gc_count / total_length) * 100

def is_valid_dna(dna_sequence):
    valid_nucleotides = set('ACGT')
    return all(nucleotide in valid_nucleotides for nucleotide in dna_sequence)

def transcribe_dna_to_rna(dna_sequence):
    return dna_sequence.replace('T', 'U')

def complement_dna_strand(dna_sequence):
    complement_map = {'A': 'T', 'T': 'A', 'C': 'G', 'G': 'C'}
    return ''.join(complement_map[nucleotide] for nucleotide in dna_sequence)

def main():
    sample_dna = "ATCGCTAGCTAGCTA"
    print("Nucleotide Counts:", count_nucleotides(sample_dna))
    print("GC Content:", gc_content(sample_dna))
    print("Valid DNA:", is_valid_dna(sample_dna))
    print("RNA Transcription:", transcribe_dna_to_rna(sample_dna))
    print("Complementary Strand:", complement_dna_strand(sample_dna))

if __name__ == "__main__":
    main()

