class Employee:
    def __init__(self, name, manager=None):
        self.name = name
        self.manager = manager
        self.subordinates = []
        self.level = 0
        self.total_subordinates = 0
        self.manager_chain = []

def build_hierarchy(employees):
    employee_dict = {emp.name: emp for emp in employees}

    for emp in employees:
        if emp.manager:
            manager_emp = employee_dict.get(emp.manager)
            if manager_emp:
                manager_emp.subordinates.append(emp)

def calculate_hierarchy_details(employees):
    def dfs_level(employee, current_level):
        employee.level = current_level
        for subordinate in employee.subordinates:
            dfs_level(subordinate, current_level + 1)

    def dfs_subordinates(employee):
        total_subs = len(employee.subordinates)
        for subordinate in employee.subordinates:
            total_subs += dfs_subordinates(subordinate)
        employee.total_subordinates = total_subs
        return total_subs

    def dfs_manager_chain(employee):
        current = employee
        chain = []
        while current.manager:
            current = employee_dict.get(current.manager)
            chain.append(current.name)
        employee.manager_chain = chain

    employee_dict = {emp.name: emp for emp in employees}

    ceo = next(emp for emp in employees if not emp.manager)
    dfs_level(ceo, 1)
    dfs_subordinates(ceo)

    for emp in employees:
        dfs_manager_chain(emp)

def print_hierarchy_details(employees):
    for emp in employees:
        print(f"Employee: {emp.name}")
        print(f"  Hierarchy Level: {emp.level}")
        print(f"  Total Subordinates: {emp.total_subordinates}")
        print(f"  Manager Chain: {' -> '.join(emp.manager_chain)}")
        print()

def main():
    employees = [
        Employee("Alice"),  # CEO
        Employee("Bob", "Alice"),
        Employee("Charlie", "Alice"),
        Employee("David", "Bob"),
        Employee("Eve", "Bob"),
        Employee("Frank", "Charlie"),
        Employee("Grace", "Charlie")
    ]

    build_hierarchy(employees)
    calculate_hierarchy_details(employees)
    print_hierarchy_details(employees)

if __name__ == "__main__":
    main()

