from collections import defaultdict

class OrganizationAnalyzer:
    def __init__(self, org_data):
        self.org_data = org_data
        self.hierarchy_levels = {}
        self.subordinate_counts = {}
        self.management_chain = {}

    def analyze_hierarchy(self):
        def find_hierarchy_level(employee, current_level=1):
            self.hierarchy_levels[employee] = current_level
            for subordinate in self.org_data.get(employee, []):
                find_hierarchy_level(subordinate, current_level + 1)

        ceo = self._find_ceo()
        find_hierarchy_level(ceo)

    def count_subordinates(self):
        def count_total_subordinates(employee):
            direct_subs = self.org_data.get(employee, [])
            total_subs = len(direct_subs)
            for sub in direct_subs:
                total_subs += count_total_subordinates(sub)
            self.subordinate_counts[employee] = total_subs
            return total_subs

        ceo = self._find_ceo()
        count_total_subordinates(ceo)

    def trace_management_chain(self):
        def trace_chain(employee, chain=None):
            if chain is None:
                chain = []

            for manager, subordinates in self.org_data.items():
                if employee in subordinates:
                    chain.append(manager)
                    return trace_chain(manager, chain)

            self.management_chain[employee] = chain
            return chain

        for employee in self.org_data.keys():
            trace_chain(employee)

    def _find_ceo(self):
        all_employees = set(self.org_data.keys())
        all_subordinates = set(sub for subs in self.org_data.values() for sub in subs)
        return list(all_employees - all_subordinates)[0]

    def print_results(self):
        print("Hierarchy Levels:")
        for emp, level in sorted(self.hierarchy_levels.items()):
            print(f"{emp}: Level {level}")

        print("\nSubordinate Counts:")
        for emp, count in sorted(self.subordinate_counts.items()):
            print(f"{emp}: {count} subordinates")

        print("\nManagement Chains:")
        for emp, chain in sorted(self.management_chain.items()):
            print(f"{emp}: {' -> '.join(reversed(chain))}")

def main():
    org_structure = {
        "CEO": ["CTO", "CFO", "COO"],
        "CTO": ["Head of Engineering", "Head of Product"],
        "CFO": ["Head of Finance"],
        "COO": ["Head of HR", "Head of Operations"],
        "Head of Engineering": ["Senior Engineer 1", "Senior Engineer 2"],
        "Head of Product": ["Product Manager 1"],
        "Head of Finance": ["Financial Analyst"],
        "Head of HR": ["HR Manager"],
        "Head of Operations": ["Operations Manager"]
    }

    analyzer = OrganizationAnalyzer(org_structure)
    analyzer.analyze_hierarchy()
    analyzer.count_subordinates()
    analyzer.trace_management_chain()
    analyzer.print_results()

if __name__ == "__main__":
    main()

