        if name not in self.employees:
            return 0

        total_subordinates = len(self.employees[name]['subordinates'])
        for sub in self.employees[name]['subordinates']:
            total_subordinates += self.count_subordinates(sub)

        return total_subordinates

    def get_management_chain(self, name):
        if name not in self.employees:
            return []

        chain = [name]
        current = name
        while self.employees[current]['manager']:
            current = self.employees[current]['manager']
            chain.append(current)

