import networkx as nx

def create_organizational_hierarchy(employees):
    G = nx.DiGraph()
    for employee, manager in employees:
        G.add_edge(manager, employee)
    return G

def calculate_hierarchy_depth(org_graph):
    return nx.dag_longest_path_length(org_graph)

def trace_reporting_chain(org_graph, employee):
    try:
        path = nx.shortest_path(org_graph, target=employee, source=find_ceo(org_graph))
        return path[:-1]
    except nx.NetworkXNoPath:
        return []

def find_ceo(org_graph):
    return [node for node in org_graph.nodes() if org_graph.in_degree(node) == 0][0]

def main():
    employees = [
        ('John', 'Sarah'),
        ('Mike', 'Sarah'),
        ('Tom', 'John'),
        ('Lisa', 'Mike')
    ]

    org_graph = create_organizational_hierarchy(employees)

    print("Hierarchy Depth:", calculate_hierarchy_depth(org_graph))
    print("Reporting Chain for Tom:", trace_reporting_chain(org_graph, 'Tom'))

if __name__ == '__main__':
