import random

class Employee:
    def __init__(self, name, position):
        self.name = name
        self.position = position
        self.subordinates = []

def create_organizational_hierarchy():
    ceo = Employee("John Smith", "CEO")

    departments = ["Marketing", "Sales", "Engineering", "Finance", "HR"]

    for dept in departments:
        dept_head = Employee(f"{dept} Director", f"Director of {dept}")
        ceo.subordinates.append(dept_head)

        for i in range(random.randint(3, 7)):
            employee = Employee(f"{dept} Employee {i+1}", f"{dept} Staff")
            dept_head.subordinates.append(employee)

    return ceo

def calculate_hierarchy_depth(employee):
    if not employee.subordinates:
        return 1

    max_depth = 1
    for subordinate in employee.subordinates:
        depth = 1 + calculate_hierarchy_depth(subordinate)
        max_depth = max(max_depth, depth)

    return max_depth

def main():
    org_hierarchy = create_organizational_hierarchy()
    hierarchy_depth = calculate_hierarchy_depth(org_hierarchy)
    print(f"Organizational Hierarchy Depth: {hierarchy_depth}")

if __name__ == "__main__":
    main()
