def maxSubsequence(nums1, nums2, k):
    n = len(nums1)
    answer = [0] * n

    for i in range(n):
        smaller_indices = [j for j in range(n) if nums1[j] < nums1[i]]

        if len(smaller_indices) <= k:
            answer[i] = sum(nums2[j] for j in smaller_indices)
        else:
            sorted_smaller = sorted([(nums2[j], j) for j in smaller_indices], reverse=True)
            answer[i] = sum(val for val, _ in sorted_smaller[:k])

    return answer

