def sort_and_sum(arr):
    n = len(arr)
    for i in range(n):
        for j in range(i + 1, n):
            if arr[j] < arr[i]:
                arr[i], arr[j] = arr[j], arr[i]

    total_sum = 0
    for i in range(n):
        total_sum += arr[i] * (i + 1)

    return total_sum

