class Solution:
    def mergeStrings(self, str1: str, str2: str) -> str:
        i, j = 0, 0
        result = []
        while i < len(str1) and j < len(str2):
            if str1[i] < str2[j]:
                result.append(str1[i])
                i += 1
            else:
                result.append(str2[j])
                j += 1
        result.extend(str1[i:])
        result.extend(str2[j:])
        return ''.join(result)

