def smallestString(str1, str2):
    from itertools import permutations

    chars1 = list(str1)
    chars2 = list(str2)

    all_chars = chars1 + chars2

    for length in range(len(all_chars), 0, -1):
        for perm in permutations(all_chars, length):
            candidate = ''.join(perm)
            if len(set(candidate)) == len(candidate):
                if all(c in candidate for c in chars1) and all(c in candidate for c in chars2):
                    return candidate

    return ""

