import itertools

def find_anagrams(word1, word2):
    if len(word1) != len(word2):
        return None

    word1_sorted = sorted(word1)
    word2_sorted = sorted(word2)

    if word1_sorted == word2_sorted:
        return word1, word2

    for perm in itertools.permutations(word1):
        candidate = ''.join(perm)
        if candidate == word2:
            return candidate, word2

    return None

def main():
    try:
        result = find_anagrams('draw', 'ward')
        if result:
            print(f"Anagrams found: {result[0]} and {result[1]}")
        else:
            print("No anagrams found")
    except Exception as e:
        print(f"An error occurred: {e}")

if __name__ == '__main__':
    main()

