def rearrange_letters(s1, s2):
    s1_chars = sorted(s1.lower())
    s2_chars = sorted(s2.lower())

    if len(s1_chars) != len(s2_chars):
        return None

    for i in range(len(s1_chars)):
        if s1_chars[i] != s2_chars[i]:
            return None

    return s2

