        for i in range(n):
            dp[i][i] = 1

        # Compute cost of changing characters to match
        def cost(s, i, j):
            return sum(s[x] != s[j] for x in range(i, j))

        # Build palindrome subsequence lengths
        for length in range(2, n + 1):
            for i in range(n - length + 1):
                j = i + length - 1

                if s[i] == s[j]:
                    dp[i][j] = dp[i+1][j-1] + 2 if i + 1 <= j - 1 else 2
                else:
                    dp[i][j] = max(dp[i+1][j], dp[i][j-1])

                # Check if we can transform to palindrome within k operations
                if cost(s, i, j) <= k:
                    dp[i][j] = max(dp[i][j], length)

