def longestSubsequence(s, k):
    n = len(s)
    if k == 0:
        return n

    def count_changes(subsequence):
        changes = 0
        for i in range(len(subsequence)):
            if subsequence[i] != s[i]:
                changes += 1
        return changes

    max_length = 0
    for length in range(1, n + 1):
        for subsequence in generate_subsequences(s, length):
            if count_changes(subsequence) <= k:
                max_length = max(max_length, length)

    return max_length

def generate_subsequences(s, length):
    def backtrack(start, current):
        if len(current) == length:
            yield current
            return

        for i in range(start, len(s)):
            current.append(s[i])
            yield from backtrack(i + 1, current)
            current.pop()

    return backtrack(0, [])

