def transform_string(s, k):
    result = ""
    for char in s:
        if char.isalpha():
            shifted = ord(char) + k
            if char.isupper():
                shifted = (shifted - 65) % 26 + 65
            else:
                shifted = (shifted - 97) % 26 + 97
            result += chr(shifted)
        else:
            result += char
    return result

def count_transformations(s, k):
    unique_transformations = set()
    for i in range(len(s)):
        transformed = transform_string(s[i:] + s[:i], k)
        unique_transformations.add(transformed)
    return len(unique_transformations)

