import random

def manipulate_string(s, k):
    result = ""
    for char in s:
        if char.isalpha():
            shift = random.randint(1, k)
            if char.isupper():
                result += chr((ord(char) - ord('A') + shift) % 26 + ord('A'))
            else:
                result += chr((ord(char) - ord('a') + shift) % 26 + ord('a'))
        else:
            result += char
    return result

def process_string(s, k):
    length = len(s)
    start_index = random.randint(0, length - 1)
    end_index = random.randint(start_index, length)
    substring = s[start_index:end_index]
    modified_substring = manipulate_string(substring, k)
    return s[:start_index] + modified_substring + s[end_index:]

def main():
    original_string = input("Enter a string: ")
    key = int(input("Enter a key value: "))

    processed_string = process_string(original_string, key)
    print("Processed string:", processed_string)

