def calculate_average_rise(data):
    if len(data) < 2:
        return 0

    total_rise = 0
    for i in range(1, len(data)):
        rise = data[i] - data[i-1]
        total_rise += rise

    average_rise = total_rise / (len(data) - 1)
    return average_rise

def process_data(data):
    if not data:
        return 0

    avg_rise = calculate_average_rise(data)
    return avg_rise

