import json
from collections import defaultdict

class OrganizationAnalyzer:
    def __init__(self, org_data):
        self.org_data = org_data
        self.hierarchy_levels = {}
        self.subordinate_counts = {}
        self.manager_chains = {}
        self.build_hierarchy()

    def build_hierarchy(self):
        # Create reverse mapping for quick lookups
        employee_to_manager = {emp['name']: emp.get('manager') for emp in self.org_data}

        # Find CEO (employee with no manager)
        self.ceo = next(emp['name'] for emp in self.org_data if not emp.get('manager'))

        # Calculate hierarchy levels
        self._calculate_levels(self.ceo, 1)

        # Calculate subordinate counts
        self._calculate_subordinates()

        # Calculate manager chains
        self._calculate_manager_chains()

    def _calculate_levels(self, employee, level):
        self.hierarchy_levels[employee] = level

        # Recursively calculate levels for direct reports
        direct_reports = [emp['name'] for emp in self.org_data if emp.get('manager') == employee]
        for report in direct_reports:
            self._calculate_levels(report, level + 1)

    def _calculate_subordinates(self):
        # Initialize subordinate counts
        for emp in self.org_data:
            self.subordinate_counts[emp['name']] = 0

        # Recursively count subordinates
        def count_subordinates(employee):
            direct_reports = [emp['name'] for emp in self.org_data if emp.get('manager') == employee]
            total_subordinates = len(direct_reports)

            for report in direct_reports:
                total_subordinates += count_subordinates(report)

            self.subordinate_counts[employee] = total_subordinates
            return total_subordinates

        count_subordinates(self.ceo)

    def _calculate_manager_chains(self):
        def trace_manager_chain(employee):
            chain = []
            current = employee

            while current in [emp['name'] for emp in self.org_data]:
                manager = next((emp.get('manager') for emp in self.org_data if emp['name'] == current), None)
                if not manager:
                    break
                chain.append(manager)
                current = manager

            return chain

        for emp in self.org_data:
            self.manager_chains[emp['name']] = trace_manager_chain(emp['name'])

    def get_hierarchy_levels(self):
        return self.hierarchy_levels

    def get_subordinate_counts(self):
        return self.subordinate_counts

    def get_manager_chains(self):
        return self.manager_chains

def main():
    # Example organization data
    org_data = [
        {"name": "Alice", "manager": None},  # CEO
        {"name": "Bob", "manager": "Alice"},
        {"name": "Charlie", "manager": "Alice"},
        {"name": "David", "manager": "Bob"},
        {"name": "Eve", "manager": "Bob"},
        {"name": "Frank", "manager": "Charlie"},
        {"name": "Grace", "manager": "Charlie"}
    ]

    # Create analyzer
    analyzer = OrganizationAnalyzer(org_data)

    # Print results
    print("Hierarchy Levels:", json.dumps(analyzer.get_hierarchy_levels(), indent=2))
    print("\nSubordinate Counts:", json.dumps(analyzer.get_subordinate_counts(), indent=2))
    print("\nManager Chains:", json.dumps(analyzer.get_manager_chains(), indent=2))

if __name__ == "__main__":
    main()

