        employee = self.employees.get(name)
        if employee:
            return len(employee.subordinates)
        return 0

    def get_hierarchy_levels(self):
        levels = {}
        def dfs(employee, level):
            if level not in levels:
                levels[level] = []
            levels[level].append(employee.name)
            for subordinate in employee.subordinates:
                dfs(subordinate, level + 1)

        if self.ceo:
            dfs(self.ceo, 1)
        return levels

