        self.employees = {}

    def add_employee(self, name, employee_id, manager_id=None):
        employee = Employee(name, employee_id, manager_id)
        self.employees[employee_id] = employee

        if manager_id and manager_id in self.employees:
            self.employees[manager_id].subordinates.append(employee)

    def get_employee(self, employee_id):
        return self.employees.get(employee_id)

    def get_manager(self, employee_id):
        employee = self.get_employee(employee_id)
        return self.get_employee(employee.manager_id) if employee and employee.manager_id else None

    def get_subordinates(self, employee_id):
        employee = self.get_employee(employee_id)
        return employee.subordinates if employee else []

