def find_common_strings(arr):
    if not arr or len(arr) == 0:
        return []

    common_strings = []
    first_string = arr[0]

    for char in first_string:
        is_common = True
        for string in arr[1:]:
            if char not in string:
                is_common = False
                break

        if is_common and char != '':
            common_strings.append(char)

    return common_strings

