class Spreadsheet:
    def __init__(self):
        self.cells = {}
        self.dependencies = {}

    def set_cell(self, cell: str, value: Union[int, str]) -> None:
        self.cells[cell] = value
        if isinstance(value, str) and value.startswith('='):
            self.dependencies[cell] = self.parse_formula(value[1:])
        else:
            self.dependencies[cell] = []
        self.update_dependents(cell)

    def get_cell(self, cell: str) -> int:
        value = self.cells.get(cell, 0)
        if isinstance(value, int):
            return value
        return self.evaluate_formula(value[1:])

    def parse_formula(self, formula: str) -> List[str]:
        # Parse the formula and return a list of referenced cells
        # This is a simplified parser; a full implementation would handle more cases
        tokens = formula.replace('+', ' ').replace('-', ' ').replace('*', ' ').replace('/', ' ').split()
        return [token for token in tokens if token.isalpha()]

    def evaluate_formula(self, formula: str) -> int:
        # Evaluate the formula by replacing cell references with their values
        for cell in self.parse_formula(formula):
            formula = formula.replace(cell, str(self.get_cell(cell)))
        try:
            return eval(formula)
        except Exception as e:
            raise ValueError(f'Invalid formula: {formula}') from e

    def update_dependents(self, cell: str) -> None:
        # Update all cells that depend on the given cell
        for dependent, refs in self.dependencies.items():
            if cell in refs:
                self.cells[dependent] = '=' + ' '.join(str(self.get_cell(ref)) if ref == cell else ref for ref in refs)

