class Spreadsheet:
    def __init__(self):
        self._cells = {}

    def set_cell(self, cell: str, value: Union[int, str]):
        self._cells[cell] = value

    def get_cell(self, cell: str) -> int:
        value = self._cells.get(cell)
        if value is None:
            return 0

        if isinstance(value, int):
            return value

        if isinstance(value, str) and value.startswith('='):
            return self._evaluate_formula(value[1:])

        return 0

    def _evaluate_formula(self, formula: str) -> int:
        try:
            tokens = re.findall(r'[A-Z]\d+|\d+|[+\-*/]', formula)
            result = self._process_tokens(tokens)
            return int(result)
        except:
            return 0

    def _process_tokens(self, tokens):
        while len(tokens) > 1:
            for i in range(len(tokens)):
                if tokens[i] in ['+', '-', '*', '/']:
                    left = self._resolve_token(tokens[i-1])
                    right = self._resolve_token(tokens[i+1])

                    if tokens[i] == '+':
                        result = left + right
                    elif tokens[i] == '-':
                        result = left - right
                    elif tokens[i] == '*':
                        result = left * right
                    elif tokens[i] == '/':
                        result = left / right

                    tokens = tokens[:i-1] + [str(int(result))] + tokens[i+2:]
                    break

        return int(tokens[0])

    def _resolve_token(self, token):
        if re.match(r'[A-Z]\d+', token):
            return self.get_cell(token)
        return int(token)

