class Employee:
    def __init__(self, name):
        self.name = name
        self.manager = None
        self.direct_reports = []
        self.hierarchy_grade = 0
        self.subordinate_count = 0
        self.manager_chain = []

class Organization:
    def __init__(self):
        self.employees = {}
        self.ceo = None

    def add_employee(self, name):
        if name not in self.employees:
            employee = Employee(name)
            self.employees[name] = employee
        return self.employees[name]

    def set_manager(self, employee_name, manager_name):
        employee = self.employees[employee_name]
        manager = self.employees[manager_name]
        employee.manager = manager
        manager.direct_reports.append(employee)

    def calculate_hierarchy_grades(self):
        def dfs_grade(employee, current_grade):
            employee.hierarchy_grade = current_grade
            for report in employee.direct_reports:
                dfs_grade(report, current_grade + 1)

        if self.ceo:
            dfs_grade(self.ceo, 1)

    def calculate_subordinate_counts(self):
        def dfs_count(employee):
            count = len(employee.direct_reports)
            for report in employee.direct_reports:
                count += dfs_count(report)
            employee.subordinate_count = count
            return count

        if self.ceo:
            dfs_count(self.ceo)

    def calculate_manager_chains(self):
        def trace_managers(employee):
            chain = []
            current = employee.manager
            while current:
                chain.append(current.name)
                current = current.manager
            employee.manager_chain = chain

        for employee in self.employees.values():
            trace_managers(employee)

    def set_ceo(self, name):
        self.ceo = self.employees[name]

    def analyze_hierarchy(self):
        self.calculate_hierarchy_grades()
        self.calculate_subordinate_counts()
        self.calculate_manager_chains()

    def print_hierarchy_details(self):
        for name, employee in sorted(self.employees.items()):
            print(f"Employee: {name}")
            print(f"  Hierarchy Grade: {employee.hierarchy_grade}")
            print(f"  Subordinate Count: {employee.subordinate_count}")
            print(f"  Manager Chain: {' -> '.join(employee.manager_chain)}")
            print()

def main():
    org = Organization()

    org.add_employee("Alice")  # CEO
    org.set_ceo("Alice")

    org.add_employee("Bob")
    org.add_employee("Charlie")
    org.add_employee("David")
    org.add_employee("Eve")
    org.add_employee("Frank")
    org.add_employee("Grace")

    org.set_manager("Bob", "Alice")
    org.set_manager("Charlie", "Alice")
    org.set_manager("David", "Bob")
    org.set_manager("Eve", "Bob")
    org.set_manager("Frank", "Charlie")
    org.set_manager("Grace", "Charlie")

    org.analyze_hierarchy()
    org.print_hierarchy_details()

if __name__ == "__main__":
    main()

