class Employee:
    def __init__(self, name):
        self.name = name
        self.manager = None
        self.direct_reports = []

def build_hierarchy(employees):
    hierarchy = {}
    for emp in employees:
        hierarchy[emp.name] = emp
    return hierarchy

def calculate_hierarchy_levels(hierarchy, ceo):
    levels = {}
    def dfs_level(employee, current_level):
        levels[employee.name] = current_level
        for report in employee.direct_reports:
            dfs_level(report, current_level + 1)

    dfs_level(ceo, 1)
    return levels

def count_subordinates(hierarchy):
    subordinate_counts = {}
    def count_subs(employee):
        total_subs = len(employee.direct_reports)
        for report in employee.direct_reports:
            total_subs += count_subs(report)
        subordinate_counts[employee.name] = total_subs
        return total_subs

    for emp in hierarchy.values():
        if emp.manager is None:
            count_subs(emp)

    return subordinate_counts

def get_manager_chain(hierarchy, employee_name):
    employee = hierarchy[employee_name]
    chain = []
    current = employee

    while current.manager:
        chain.append(current.manager.name)
        current = current.manager

    return chain

def main():
    # Example usage
    ceo = Employee("John")

    alice = Employee("Alice")
    bob = Employee("Bob")
    charlie = Employee("Charlie")

    alice.manager = ceo
    bob.manager = ceo
    charlie.manager = alice

    ceo.direct_reports = [alice, bob]
    alice.direct_reports = [charlie]

    employees = [ceo, alice, bob, charlie]
    hierarchy = build_hierarchy(employees)

    levels = calculate_hierarchy_levels(hierarchy, ceo)
    print("Hierarchy Levels:", levels)

    subordinates = count_subordinates(hierarchy)
    print("Subordinate Counts:", subordinates)

    manager_chain = get_manager_chain(hierarchy, "Charlie")
    print("Manager Chain for Charlie:", manager_chain)

if __name__ == "__main__":
    main()
