def process_queries(nums, queries):
    result = []
    for i, k in queries:
        if i < 0 or i >= len(nums):
            result.append(-1)  # Handle out-of-bounds index
        else:
            value = nums[i]
            count = 0
            for num in nums:
                if num < value:
                    count += 1
                    if count > k:
                        break
            if count <= k:
                result.append(-1)
            else:
                # Find the k-th smallest number less than value
                less_than = [num for num in nums if num < value]
                less_than.sort()
                if k < len(less_than):
                    result.append(less_than[k])
                else:
                    result.append(-1)
    return result

