from collections import defaultdict

def longest_special_path(tree, nums):
    n = len(nums)
    if n == 0:
        return 0

    # Build the adjacency list for the tree
    adj = defaultdict(list)
    for u, v in tree:
        adj[u].append(v)
        adj[v].append(u)

    max_length = 0

    def dfs(node, parent, freq):
        nonlocal max_length
        num = nums[node]
        freq[num] += 1

        if freq[num] <= 2:
            current_length = sum(freq.values())
            if current_length > max_length:
                max_length = current_length

        for neighbor in adj[node]:
            if neighbor != parent:
                dfs(neighbor, node, freq.copy())

    # Start DFS from each node to explore all possible paths
    for start_node in range(n):
        dfs(start_node, -1, defaultdict(int))

    return max_length

