from collections import defaultdict, deque

def longest_special_path(tree, nums):
    if not tree:
        return 0

    max_len = 1
    adj = defaultdict(list)
    for u, v in tree:
        adj[u].append(v)
        adj[v].append(u)

    n = len(nums)
    for start in range(n):
        visited = [False] * n
        q = deque()
        q.append((start, 1))
        visited[start] = True
        current_val = nums[start]

        while q:
            node, length = q.popleft()
            if length > max_len:
                max_len = length

            for neighbor in adj[node]:
                if not visited[neighbor] and nums[neighbor] != current_val:
                    visited[neighbor] = True
                    q.append((neighbor, length + 1))

    return max_len

