from collections import deque

def longest_special_path(tree, nums):
    n = len(tree)
    if n == 0:
        return 0

    max_length = 1
    in_degree = [0] * n
    adj = [[] for _ in range(n)]

    for u in range(n):
        for v in tree[u]:
            adj[u].append(v)
            in_degree[v] += 1

    q = deque()
    dp = [1] * n

    for i in range(n):
        if in_degree[i] == 0:
            q.append(i)

    while q:
        u = q.popleft()
        for v in adj[u]:
            if nums[v] > nums[u]:
                if dp[v] < dp[u] + 1:
                    dp[v] = dp[u] + 1
                    max_length = max(max_length, dp[v])
            in_degree[v] -= 1
            if in_degree[v] == 0:
                q.append(v)

    return max_length

