from collections import defaultdict

class TreeNode:
    def __init__(self, val=0, children=None):
        self.val = val
        self.children = children if children is not None else []

def build_tree(n, edges, nums):
    tree = defaultdict(list)
    for u, v in edges:
        tree[u].append(v)
        tree[v].append(u)

    nodes = [TreeNode(nums[i]) for i in range(n)]
    visited = [False] * n
    stack = [(0, None)]

    while stack:
        node, parent = stack.pop()
        if visited[node]:
            continue
        visited[node] = True
        for neighbor in tree[node]:
            if neighbor != parent:
                nodes[node].children.append(nodes[neighbor])
                stack.append((neighbor, node))

    return nodes[0] if n > 0 else None

