            for ref_cell in referenced_cells:
                if ref_cell not in self.dependents:
                    self.dependents[ref_cell] = set()
                self.dependents[ref_cell].add(cell)
        else:
            self.dependencies.pop(cell, None)
            self._notify_dependents(cell)

    def get_cell(self, cell):
        if cell not in self.cells:
            return 0
        value = self.cells[cell]
        if isinstance(value, str) and value.startswith('='):
