                return self._evaluate_formula(value[1:])
            except:
                return 0
        elif isinstance(value, int):
            return value
        else:
            return 0

    def _parse_formula(self, formula):
        import re
        return set(re.findall(r'[A-Za-z]+\d+', formula))

    def _evaluate_formula(self, formula):
        try:
            return eval(formula, {}, self)
        except:
            return 0

    def __getitem__(self, cell):
        return self.get_cell(cell)

