            for ref_cell in self.dependencies[cell]:
                if ref_cell in self.dependents:
                    self.dependents[ref_cell].discard(cell)
                    if not self.dependents[ref_cell]:
                        del self.dependents[ref_cell]
            del self.dependencies[cell]

    def _notify_dependents(self, cell):
