            raise ValueError(f"Cell {cell} not found or has no value")
        return self.cell_values[cell]

    def _parse_formula(self, cell: str, formula: str):
        referenced_cells = set()
        tokens = formula.split()
        for token in tokens:
            if token.isalpha() and token.isupper():
                referenced_cells.add(token)

        self.dependencies[cell] = referenced_cells
        for ref_cell in referenced_cells:
            if ref_cell not in self.cell_values:
                self.cell_values[ref_cell] = 0

    def _update_dependencies(self, cell: str):
        for dependent_cell in list(self.dependencies.keys()):
            if cell in self.dependencies[dependent_cell]:
                self._evaluate_cell(dependent_cell)

    def _evaluate_cell(self, cell: str):
        if cell not in self.cells:
            return

        value = self.cells[cell]
        if isinstance(value, int):
            self.cell_values[cell] = value
            self._update_dependencies(cell)
        elif isinstance(value, str):
            try:
                formula = value
                referenced_cells = self.dependencies.get(cell, set())
                local_vars = {ref: self.cell_values.get(ref, 0) for ref in referenced_cells}
                self.cell_values[cell] = eval(formula, {}, local_vars)
                self._update_dependencies(cell)
            except:
                self.cell_values[cell] = 0

