                self.cache[cell] = result
                return result
            except Exception as e:
                return f"ERROR: {str(e)}"
        else:
            return value

    def _evaluate_expression(self, expr: str) -> int:
        try:
            # Handle simple arithmetic expressions
            tokens = expr.split()
            stack = []
            for token in tokens:
                if token in '+-*/':
                    b = stack.pop()
                    a = stack.pop()
                    if token == '+':
                        stack.append(a + b)
                    elif token == '-':
                        stack.append(a - b)
                    elif token == '*':
                        stack.append(a * b)
                    elif token == '/':
                        stack.append(a // b)
                elif token.isdigit():
                    stack.append(int(token))
                else:
                    # Assume it's a cell reference
                    val = self.get_cell(token)
                    if isinstance(val, int):
                        stack.append(val)
                    else:
                        raise ValueError(f"Invalid cell reference or value: {token}")
            return stack[0] if stack else 0
        except Exception as e:
            raise ValueError(f"Invalid expression: {expr}") from e

    def _invalidate_cache(self, cell: str):
        if cell in self.cache:
            del self.cache[cell]
        # Invalidate dependent cells
        for dependent in self.dependencies.get(cell, []):
            self._invalidate_cache(dependent)

