class Spreadsheet:
    def __init__(self, rows: int, cols: int):
        self.rows = rows
        self.cols = cols
        self.data = [[0 for _ in range(cols)] for _ in range(rows)]
        self.formulas = {}

    def set_value(self, row: int, col: int, value: int) -> None:
        if not (0 <= row < self.rows and 0 <= col < self.cols):
            raise ValueError("Invalid cell coordinates")
        self.data[row][col] = value
        if (row, col) in self.formulas:
            del self.formulas[(row, col)]

    def get_value(self, row: int, col: int) -> int:
        if not (0 <= row < self.rows and 0 <= col < self.cols):
            raise ValueError("Invalid cell coordinates")
        if (row, col) in self.formulas:
            return self.evaluate_formula(row, col)
        return self.data[row][col]

    def set_formula(self, row: int, col: int, formula: str) -> None:
        if not (0 <= row < self.rows and 0 <= col < self.cols):
            raise ValueError("Invalid cell coordinates")
        self.formulas[(row, col)] = formula
        self.data[row][col] = 0

    def evaluate_formula(self, row: int, col: int) -> int:
        formula = self.formulas[(row, col)]
        try:
            return eval(formula, {}, {'cells': self})
        except:
            return 0

    def sum(self, start_row: int, start_col: int, end_row: int, end_col: int) -> int:
        total = 0
        for r in range(start_row, end_row + 1):
            for c in range(start_col, end_col + 1):
                total += self.get_value(r, c)
        return total

    def __repr__(self) -> str:
        return '\n'.join([' '.join(map(str, row)) for row in self.data])

