def largestAlmostMissing(nums, k):
    if k <= 0 or k > len(nums):
        return -1

    # To keep track of the count of each number in the current window
    window_counts = defaultdict(int)
    # To keep track of the count of each number across all windows
    total_counts = defaultdict(int)

    # Initialize the first window
    for i in range(k):
        x = nums[i]
        window_counts[x] += 1

    # Update total_counts with the first window
    for x in window_counts:
        if window_counts[x] == 1:
            total_counts[x] += 1

    # Slide the window through the array
    for i in range(k, len(nums)):
        # Remove the leftmost element of the previous window
        left = nums[i - k]
        window_counts[left] -= 1
        if window_counts[left] == 0:
            del window_counts[left]
            if left in total_counts:
                total_counts[left] -= 1
                if total_counts[left] == 0:
                    del total_counts[left]
