            total_counts[left] += 1

        # Add the new element to the window
        right = nums[i]
        window_counts[right] += 1
        if window_counts[right] == 1:
            total_counts[right] += 1
        elif window_counts[right] == 2:
            total_counts[right] -= 1
            if total_counts[right] == 0:
                del total_counts[right]

    # Find the largest almost missing integer
    if not total_counts:
        return -1
    return max(total_counts.keys())

