from collections import defaultdict

def find_special_paths(n, nums, edges):
    tree = defaultdict(list)
    for u, v in edges:
        tree[u].append(v)
        tree[v].append(u)

    max_length = 0
    min_nodes_in_max_path = float('inf')

    def dfs(node, parent, freq):
        nonlocal max_length, min_nodes_in_max_path
        current_num = nums[node]
        freq[current_num] += 1
        path_valid = freq[current_num] <= 2

        current_length = 1
        for neighbor in tree[node]:
            if neighbor == parent:
                continue
            child_length = dfs(neighbor, node, freq.copy() if not path_valid else freq)
            if path_valid:
                current_length += child_length

        if path_valid:
            if current_length > max_length:
                max_length = current_length
                min_nodes_in_max_path = current_length
            elif current_length == max_length:
                if current_length < min_nodes_in_max_path:
                    min_nodes_in_max_path = current_length

        return current_length if path_valid else 0

    for i in range(n):
        dfs(i, -1, defaultdict(int))

    return max_length, min_nodes_in_max_path

