def longest_subsequence(nums):
    if not nums:
        return 0, 0

    max_len = 1
    current_len = 1
    max_count = 1
    current_count = 1

    for i in range(1, len(nums)):
        if nums[i] == nums[i - 1]:
            current_count += 1
            current_len += 1
        else:
            if current_len > max_len:
                max_len = current_len
                max_count = current_count
            elif current_len == max_len:
                max_count = max(max_count, current_count)
            current_len = 1
            current_count = 1

    if current_len > max_len:
        max_len = current_len
        max_count = current_count
    elif current_len == max_len:
        max_count = max(max_count, current_count)

    return max_len, max_count

