def calculate_vowel_count(text):
    vowels = "aeiouAEIOU"
    count = 0
    for char in text:
        if char in vowels:
            count += 1
    return count

input_text = input("Enter a string: ")
vowel_count = calculate_vowel_count(input_text)
print(f"The number of vowels in the string is: {vowel_count}")

